import React, { useState } from 'react';
import { AlertTriangle, Shield, Eye, CreditCard, Target, Lock, ChevronDown, ChevronRight, ExternalLink, BarChart3, TrendingUp, Activity } from 'lucide-react';

const Dashboard = () => {
  const [expandedPattern, setExpandedPattern] = useState(null);

  // Parse the data from the document
  const analysisData = {
    url: "https://www.goibibo.com/",
    timestamp: "2025-06-11T07:21:52.606Z",
    totalPatterns: 4,
    riskScore: 48,
    patterns: [
      {
        type: "creditCardForFreeTrial",
        severity: "high",
        confidence: 0.8,
        description: "Requires credit card information for free trial",
        evidence: {
          freeTrialMentions: ["free"],
          paymentMentions: ["visa"],
          suspiciousButtons: []
        },
        location: "Form fields and payment section",
        exactLocations: [
          {
            term: "free",
            context: "alid till 15th Jun'25\n\nTrains\n\nUp to ₹300 OFF* on Free Cancellation feature & more on trains!\n\nValid til",
            exactMatch: "Free"
          },
          {
            term: "visa",
            context: "nsurance for Asia |\nTravel Insurance for Schengen Visa |\nTravel Insurance for Bhutan |\nTravel Insurance ",
            exactMatch: "Visa"
          }
        ]
      },
      {
        type: "manipulativeButtons",
        severity: "medium",
        confidence: 0.6,
        description: "Manipulative button design - positive actions emphasized, negative options hidden",
        evidence: [
          {
            positiveButtons: ["Continue"],
            missingNegativeOptions: ["no", "decline", "cancel", "skip"],
            negativeInText: true
          }
        ],
        location: "Interactive buttons"
      },
      {
        type: "fomo",
        severity: "medium",
        confidence: 0.5,
        description: "Uses fear of missing out tactics to pressure users",
        evidence: {
          fomoScore: 5,
          instances: [
            {
              location: "rawText",
              keyword: "only",
              context: "mbai\nAuckland\nSat, 16 Aug\n\nUpto ₹1700* Off\n\nValid only on app\n\nScan to download\n\nOffers For You\nAll\nBank"
            },
            {
              location: "rawText",
              keyword: "exclusive",
              context: "oking\n\nMy Trips\n\nManage Your Trips\nLogin / Signup\nExclusive Offers\nEarn goCash\nManage Your Trips\nLogin / Sign"
            }
          ]
        },
        location: "Headlines, alerts, and promotional content"
      },
      {
        type: "privacyZuckering",
        severity: "medium",
        confidence: 0.6,
        description: "Tricks users into sharing more personal information than necessary",
        evidence: {
          privacyMentions: ["privacy", "privacy"],
          trickyLanguage: ["you agree"],
          disguisedCollection: []
        },
        location: "Privacy policies and consent forms"
      }
    ]
  };

  const getSeverityColor = (severity) => {
    switch (severity) {
      case 'high': return 'bg-gradient-to-r from-red-500/20 to-pink-500/20 text-red-300 border-red-500/30';
      case 'medium': return 'bg-gradient-to-r from-yellow-500/20 to-orange-500/20 text-yellow-300 border-yellow-500/30';
      case 'low': return 'bg-gradient-to-r from-green-500/20 to-emerald-500/20 text-green-300 border-green-500/30';
      default: return 'bg-gradient-to-r from-gray-500/20 to-slate-500/20 text-gray-300 border-gray-500/30';
    }
  };

  const getPatternIcon = (type) => {
    switch (type) {
      case 'creditCardForFreeTrial': return <CreditCard className="w-5 h-5" />;
      case 'manipulativeButtons': return <Target className="w-5 h-5" />;
      case 'fomo': return <AlertTriangle className="w-5 h-5" />;
      case 'privacyZuckering': return <Lock className="w-5 h-5" />;
      default: return <Eye className="w-5 h-5" />;
    }
  };

  const formatPatternName = (type) => {
    switch (type) {
      case 'creditCardForFreeTrial': return 'Credit Card for Free Trial';
      case 'manipulativeButtons': return 'Manipulative Buttons';
      case 'fomo': return 'Fear of Missing Out (FOMO)';
      case 'privacyZuckering': return 'Privacy Zuckering';
      default: return type;
    }
  };

  const getRiskLevel = (score) => {
    if (score >= 70) return { level: 'High Risk', color: 'text-red-400', bg: 'bg-gradient-to-r from-red-500/20 to-pink-500/20' };
    if (score >= 40) return { level: 'Medium Risk', color: 'text-yellow-400', bg: 'bg-gradient-to-r from-yellow-500/20 to-orange-500/20' };
    return { level: 'Low Risk', color: 'text-green-400', bg: 'bg-gradient-to-r from-green-500/20 to-emerald-500/20' };
  };

  const risk = getRiskLevel(analysisData.riskScore);

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center space-x-3 mb-4">
            <div className="p-2 bg-gradient-to-r from-purple-500 to-pink-500 rounded-lg">
              <Shield className="w-6 h-6 text-white" />
            </div>
            <h1 className="text-4xl font-bold bg-gradient-to-r from-white to-gray-300 bg-clip-text text-transparent">
              UX Pattern Detection
            </h1>
          </div>
          <p className="text-gray-400 text-lg">Analyzing dark patterns and UX issues across websites</p>
        </div>

        {/* Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-gradient-to-br from-slate-800/50 to-slate-900/50 backdrop-blur-sm rounded-2xl border border-slate-700/50 p-6 hover:border-slate-600/50 transition-all duration-300">
            <div className="flex items-center justify-between mb-4">
              <div className="p-2 bg-gradient-to-r from-blue-500 to-cyan-500 rounded-lg">
                <ExternalLink className="w-5 h-5 text-white" />
              </div>
              <div className="h-12 w-16 bg-gradient-to-r from-blue-500/20 to-cyan-500/20 rounded-lg flex items-center justify-center">
                <Activity className="w-6 h-6 text-blue-400" />
              </div>
            </div>
            <div>
              <p className="text-sm font-medium text-gray-400 mb-2">Website Analyzed</p>
              <a href={analysisData.url} target="_blank" rel="noopener noreferrer" className="text-blue-400 hover:text-blue-300 text-sm font-semibold transition-colors">
                goibibo.com
              </a>
            </div>
          </div>

          <div className="bg-gradient-to-br from-slate-800/50 to-slate-900/50 backdrop-blur-sm rounded-2xl border border-slate-700/50 p-6 hover:border-slate-600/50 transition-all duration-300">
            <div className="flex items-center justify-between mb-4">
              <div className="p-2 bg-gradient-to-r from-orange-500 to-red-500 rounded-lg">
                <AlertTriangle className="w-5 h-5 text-white" />
              </div>
              <div className="h-12 w-16 bg-gradient-to-r from-orange-500/20 to-red-500/20 rounded-lg flex items-center justify-center">
                <BarChart3 className="w-6 h-6 text-orange-400" />
              </div>
            </div>
            <div>
              <p className="text-sm font-medium text-gray-400 mb-2">Patterns Detected</p>
              <p className="text-3xl font-bold text-white">{analysisData.totalPatterns}</p>
            </div>
          </div>

          <div className="bg-gradient-to-br from-slate-800/50 to-slate-900/50 backdrop-blur-sm rounded-2xl border border-slate-700/50 p-6 hover:border-slate-600/50 transition-all duration-300">
            <div className="flex items-center justify-between mb-4">
              <div className="p-2 bg-gradient-to-r from-purple-500 to-pink-500 rounded-lg">
                <Shield className="w-5 h-5 text-white" />
              </div>
            </div>
            <div>
              <p className="text-sm font-medium text-gray-400 mb-3">Risk Score</p>
              <div className="flex items-center space-x-4">
                <div className="flex items-end space-x-2">
                  <p className="text-3xl font-bold text-white">{analysisData.riskScore}</p>
                  <p className="text-gray-400 text-sm mb-1">/100</p>
                </div>
                {/* Circular Progress */}
                <div className="relative w-16 h-16">
                  <svg className="w-16 h-16 transform -rotate-90" viewBox="0 0 64 64">
                    <circle
                      cx="32"
                      cy="32"
                      r="28"
                      stroke="currentColor"
                      strokeWidth="6"
                      fill="none"
                      className="text-slate-700"
                    />
                    <circle
                      cx="32"
                      cy="32"
                      r="28"
                      stroke="url(#gradient-purple)"
                      strokeWidth="6"
                      fill="none"
                      strokeDasharray={`${2 * Math.PI * 28}`}
                      strokeDashoffset={`${2 * Math.PI * 28 * (1 - analysisData.riskScore / 100)}`}
                      className="transition-all duration-1000 ease-out"
                      strokeLinecap="round"
                    />
                  </svg>
                  <defs>
                    <linearGradient id="gradient-purple" x1="0%" y1="0%" x2="100%" y2="0%">
                      <stop offset="0%" stopColor="#8b5cf6" />
                      <stop offset="100%" stopColor="#ec4899" />
                    </linearGradient>
                  </defs>
                </div>
              </div>
            </div>
          </div>

          <div className="bg-gradient-to-br from-slate-800/50 to-slate-900/50 backdrop-blur-sm rounded-2xl border border-slate-700/50 p-6 hover:border-slate-600/50 transition-all duration-300">
            <div className="flex items-center justify-between mb-4">
              <div className={`p-2 ${risk.color === 'text-red-400' ? 'bg-gradient-to-r from-red-500 to-pink-500' : risk.color === 'text-yellow-400' ? 'bg-gradient-to-r from-yellow-500 to-orange-500' : 'bg-gradient-to-r from-green-500 to-emerald-500'} rounded-lg`}>
                <Eye className="w-5 h-5 text-white" />
              </div>
            </div>
            <div>
              <p className="text-sm font-medium text-gray-400 mb-3">Risk Level</p>
              <div className="flex items-center space-x-4">
                <span className={`inline-flex items-center px-4 py-2 rounded-lg text-sm font-semibold ${risk.bg} ${risk.color} border border-current/20`}>
                  {risk.level}
                </span>
                {/* Circular Risk Level Indicator */}
                <div className="relative w-12 h-12">
                  <svg className="w-12 h-12 transform -rotate-90" viewBox="0 0 48 48">
                    {/* Background circles */}
                    <circle cx="24" cy="24" r="20" stroke="currentColor" strokeWidth="4" fill="none" className="text-slate-700/50" />
                    <circle cx="24" cy="24" r="15" stroke="currentColor" strokeWidth="3" fill="none" className="text-slate-700/50" />
                    <circle cx="24" cy="24" r="10" stroke="currentColor" strokeWidth="2" fill="none" className="text-slate-700/50" />

                    {/* Active risk level circles */}
                    {analysisData.riskScore >= 1 && (
                      <circle cx="24" cy="24" r="10" stroke="#10b981" strokeWidth="2" fill="none" className="transition-all duration-500" />
                    )}
                    {analysisData.riskScore >= 40 && (
                      <circle cx="24" cy="24" r="15" stroke="#f59e0b" strokeWidth="3" fill="none" className="transition-all duration-500" />
                    )}
                    {analysisData.riskScore >= 70 && (
                      <circle cx="24" cy="24" r="20" stroke="#ef4444" strokeWidth="4" fill="none" className="transition-all duration-500" />
                    )}
                  </svg>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Patterns Chart and List */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
          {/* Patterns Bar Chart */}
          <div className="bg-gradient-to-br from-slate-800/40 to-slate-900/40 backdrop-blur-sm rounded-2xl border border-slate-700/50 p-6">
            <h3 className="text-xl font-bold text-white mb-6">Pattern Distribution</h3>
            <div className="space-y-4">
              {analysisData.patterns.map((pattern, index) => {
                const maxConfidence = Math.max(...analysisData.patterns.map(p => p.confidence));
                const barWidth = (pattern.confidence / maxConfidence) * 100;

                return (
                  <div key={index} className="space-y-2">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-3">
                        <div className={`p-2 rounded-lg ${pattern.severity === 'high' ? 'bg-gradient-to-r from-red-500/20 to-pink-500/20' : pattern.severity === 'medium' ? 'bg-gradient-to-r from-yellow-500/20 to-orange-500/20' : 'bg-gradient-to-r from-green-500/20 to-emerald-500/20'}`}>
                          <div className={`w-4 h-4 ${pattern.severity === 'high' ? 'text-red-400' : pattern.severity === 'medium' ? 'text-yellow-400' : 'text-green-400'}`}>
                            {getPatternIcon(pattern.type)}
                          </div>
                        </div>
                        <span className="text-sm text-gray-300 font-medium">
                          {formatPatternName(pattern.type)}
                        </span>
                      </div>
                      <span className="text-xs text-gray-400">
                        {Math.round(pattern.confidence * 100)}%
                      </span>
                    </div>
                    <div className="w-full bg-slate-700/30 rounded-full h-2">
                      <div 
                        className={`h-2 rounded-full transition-all duration-1000 ease-out ${
                          pattern.severity === 'high' 
                            ? 'bg-gradient-to-r from-red-500 to-pink-500' 
                            : pattern.severity === 'medium' 
                              ? 'bg-gradient-to-r from-yellow-500 to-orange-500' 
                              : 'bg-gradient-to-r from-green-500 to-emerald-500'
                        }`}
                        style={{ width: `${barWidth}%` }}
                      ></div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Summary Stats */}
          <div className="bg-gradient-to-br from-slate-800/40 to-slate-900/40 backdrop-blur-sm rounded-2xl border border-slate-700/50 p-6">
            <h3 className="text-xl font-bold text-white mb-6">Pattern Summary</h3>
            <div className="space-y-6">
              {/* Severity Breakdown */}
              <div>
                <h4 className="text-sm font-medium text-gray-400 mb-3">By Severity</h4>
                <div className="space-y-3">
                  {['high', 'medium', 'low'].map(severity => {
                    const count = analysisData.patterns.filter(p => p.severity === severity).length;
                    const percentage = (count / analysisData.totalPatterns) * 100;
                    return (
                      <div key={severity} className="flex items-center justify-between">
                        <div className="flex items-center space-x-2">
                          <div className={`w-3 h-3 rounded-full ${
                            severity === 'high' ? 'bg-red-500' : 
                            severity === 'medium' ? 'bg-yellow-500' : 'bg-green-500'
                          }`}></div>
                          <span className="text-sm text-gray-300 capitalize">{severity}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm text-white font-medium">{count}</span>
                          <span className="text-xs text-gray-400">({percentage.toFixed(0)}%)</span>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>

              {/* Average Confidence */}
              <div>
                <h4 className="text-sm font-medium text-gray-400 mb-3">Average Confidence</h4>
                <div className="flex items-center space-x-3">
                  <span className="text-2xl font-bold text-white">
                    {Math.round(analysisData.patterns.reduce((acc, p) => acc + p.confidence, 0) / analysisData.patterns.length * 100)}%
                  </span>
                  <div className="flex-1 bg-slate-700/30 rounded-full h-2">
                    <div 
                      className="bg-gradient-to-r from-blue-500 to-cyan-500 h-2 rounded-full transition-all duration-1000 ease-out"
                      style={{ width: `${Math.round(analysisData.patterns.reduce((acc, p) => acc + p.confidence, 0) / analysisData.patterns.length * 100)}%` }}
                    ></div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Detailed Patterns List */}
        <div className="bg-gradient-to-br from-slate-800/40 to-slate-900/40 backdrop-blur-sm rounded-2xl border border-slate-700/50">
          <div className="px-8 py-6 border-b border-slate-700/50">
            <h2 className="text-2xl font-bold text-white mb-2">Detailed Analysis</h2>
            <p className="text-gray-400">Click on any pattern to view comprehensive details</p>
          </div>

          <div className="divide-y divide-slate-700/50">
            {analysisData.patterns.map((pattern, index) => (
              <div key={index} className="p-6 hover:bg-slate-800/30 transition-all duration-300">
                <div 
                  className="flex items-center justify-between cursor-pointer"
                  onClick={() => setExpandedPattern(expandedPattern === index ? null : index)}
                >
                  <div className="flex items-center space-x-6">
                    <div className={`p-3 rounded-xl ${pattern.severity === 'high' ? 'bg-gradient-to-r from-red-500/20 to-pink-500/20' : pattern.severity === 'medium' ? 'bg-gradient-to-r from-yellow-500/20 to-orange-500/20' : 'bg-gradient-to-r from-green-500/20 to-emerald-500/20'}`}>
                      <div className={`${pattern.severity === 'high' ? 'text-red-400' : pattern.severity === 'medium' ? 'text-yellow-400' : 'text-green-400'}`}>
                        {getPatternIcon(pattern.type)}
                      </div>
                    </div>

                    <div className="flex-1">
                      <div className="flex items-center space-x-4 mb-2">
                        <h3 className="text-xl font-semibold text-white">
                          {formatPatternName(pattern.type)}
                        </h3>
                        <span className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-semibold border ${getSeverityColor(pattern.severity)}`}>
                          {pattern.severity.charAt(0).toUpperCase() + pattern.severity.slice(1)}
                        </span>
                      </div>
                      <p className="text-gray-300 mb-3">{pattern.description}</p>
                      <div className="flex items-center space-x-6">
                        <div className="flex items-center space-x-2">
                          <div className="w-2 h-2 bg-blue-400 rounded-full"></div>
                          <span className="text-sm text-gray-400">
                            Confidence: {Math.round(pattern.confidence * 100)}%
                          </span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <div className="w-2 h-2 bg-purple-400 rounded-full"></div>
                          <span className="text-sm text-gray-400">
                            Location: {pattern.location}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="flex-shrink-0">
                    <div className="p-2 rounded-lg bg-slate-700/50 hover:bg-slate-600/50 transition-colors">
                      {expandedPattern === index ? 
                        <ChevronDown className="w-5 h-5 text-gray-400" /> : 
                        <ChevronRight className="w-5 h-5 text-gray-400" />
                      }
                    </div>
                  </div>
                </div>

                {/* Expanded Details */}
                {expandedPattern === index && (
                  <div className="mt-8 pl-16 space-y-4">
                    <div className="bg-gradient-to-r from-slate-800/50 to-slate-700/50 rounded-xl p-6 border border-slate-600/30">
                      <h4 className="font-semibold text-white mb-4 flex items-center space-x-2">
                        <div className="w-2 h-2 bg-cyan-400 rounded-full"></div>
                        <span>Evidence Found</span>
                      </h4>

                      {pattern.type === 'fomo' && pattern.evidence.instances && (
                        <div>
                          <div className="mb-4 p-3 bg-blue-500/10 rounded-lg border border-blue-500/20">
                            <span className="text-blue-300 font-medium">FOMO Score: {pattern.evidence.fomoScore}</span>
                          </div>
                          <div className="space-y-3">
                            {pattern.evidence.instances.slice(0, 3).map((instance, idx) => (
                              <div key={idx} className="bg-slate-900/50 p-4 rounded-lg border border-slate-600/30">
                                <span className="font-mono text-xs bg-gradient-to-r from-blue-500/20 to-cyan-500/20 text-blue-300 px-3 py-1 rounded-full border border-blue-500/30">
                                  {instance.keyword}
                                </span>
                                <p className="text-xs text-gray-400 mt-3 font-mono leading-relaxed">
                                  ...{instance.context.substring(0, 100)}...
                                </p>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}

                      {pattern.type === 'creditCardForFreeTrial' && pattern.exactLocations && (
                        <div className="space-y-3">
                          {pattern.exactLocations.map((location, idx) => (
                            <div key={idx} className="bg-slate-900/50 p-4 rounded-lg border border-slate-600/30">
                              <span className="font-mono text-xs bg-gradient-to-r from-red-500/20 to-pink-500/20 text-red-300 px-3 py-1 rounded-full border border-red-500/30">
                                {location.exactMatch}
                              </span>
                              <p className="text-xs text-gray-400 mt-3 font-mono leading-relaxed">
                                ...{location.context}...
                              </p>
                            </div>
                          ))}
                        </div>
                      )}

                      {pattern.type === 'manipulativeButtons' && pattern.evidence && (
                        <div className="space-y-3">
                          {pattern.evidence.map((evidence, idx) => (
                            <div key={idx} className="bg-slate-900/50 p-4 rounded-lg border border-slate-600/30">
                              <div className="space-y-2">
                                <p className="text-sm text-gray-300">
                                  <span className="text-yellow-400 font-medium">Positive buttons:</span> {evidence.positiveButtons.join(', ')}
                                </p>
                                <p className="text-sm text-gray-300">
                                  <span className="text-red-400 font-medium">Missing negative options:</span> {evidence.missingNegativeOptions.join(', ')}
                                </p>
                              </div>
                            </div>
                          ))}
                        </div>
                      )}

                      {pattern.type === 'privacyZuckering' && pattern.evidence && (
                        <div className="bg-slate-900/50 p-4 rounded-lg border border-slate-600/30">
                          <div className="space-y-2">
                            <p className="text-sm text-gray-300">
                              <span className="text-purple-400 font-medium">Privacy mentions:</span> {pattern.evidence.privacyMentions.length}
                            </p>
                            <p className="text-sm text-gray-300">
                              <span className="text-orange-400 font-medium">Tricky language:</span> {pattern.evidence.trickyLanguage.join(', ')}
                            </p>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Footer */}
        <div className="mt-8 text-center">
          <div className="inline-flex items-center space-x-2 px-4 py-2 bg-slate-800/50 rounded-full border border-slate-700/50">
            <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
            <span className="text-sm text-gray-400">
              Analysis completed on {new Date(analysisData.timestamp).toLocaleString()}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
